package Day_6.Varun;public class PrintAllSubset {    public static void main(String[] args) {        String output ="";        String input = "abc";        printAllSubset(output,input);    }    public static void printAllSubset(String outp, String inp){        if(inp.length()==0){            System.out.println(outp);            return;        }        StringBuilder out = new StringBuilder(outp);        String out1 = out.toString();        String out2 = out.append(inp.charAt(0)).toString();        String input = inp.substring(1);        printAllSubset(out1,input);        printAllSubset(out2,input);        return ;    }}