package Day_6.Varun;public class PrintAllSubset {    public static void main(String[] args) {        String output ="";        String input = "abc";        printAllSubset(output,input);    }    public static void printAllSubset(String outp, String inp){        if(inp.length()==0){            System.out.println(outp);            return;        }        StringBuilder out1 = new StringBuilder(outp);        StringBuilder out2 = new StringBuilder(outp);        out2.append(inp.charAt(0));        String input = inp.substring(1,inp.length());        printAllSubset(out1.toString(),input);        printAllSubset(out2.toString(),input);        return ;    }}